// 100% to counter Bootstrap styles and match grid.scss
.container {
    width: 100%;
    max-width: 100%;
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
    display: table;
}
.main.full {
    // float: left;
    max-width: 100%;
}
// .productCarousel {
